import {
  StyleSheet,
  Text,
  View,
  Button,
  TextInput,
  Image,
  SafeAreaView,
} from "react-native";

import HomeScreen from "./screens/HomeScreen";
import ProductScreen from "./screens/ProductScreen";
import DetailScreen from "./screens/DetailScreen";
import LoginScreen from "./screens/LoginScreen";
import RulesScreen from "./screens/RulesScreen";
import RoleScreen from "./screens/RoleScreen";
import SkillScreen from "./screens/SkillScreen";
import BookmarkScreen from "./screens/SkillScreen";
import SettingSrceen from "./screens/SettingSrceen";

import { NavigationContainer, DefaultTheme } from "@react-navigation/native";
import { createNativeStackNavigator } from "@react-navigation/native-stack";

import {
  createDrawerNavigator,
  DrawerContentScrollView,
  DrawerItem,
  DrawerItemList,
} from "@react-navigation/drawer";
import React from "react";



const MyTheme = {
  ...DefaultTheme,
  colors: {
    ...DefaultTheme.colors,
    primary: "rgb(26,45,85)",
    background: "#F2F2F2",
   
  },
};

const Stack = createNativeStackNavigator();

function ProductStack(){
  return(
      <Stack.Navigator 
      screenOptions = {{
        headerStyle: {
          backgroundColor: "#FFD66D",
        },
        headerTintColor:'#ffffff',
        headerTitleStyle:{
          fontWeigth:'bold'
        }
      }}
      >
      <Stack.Screen name='Product' component={ProductScreen}/>
      {/* <Stack.Screen name='Detail' component={DetailScreen}/> */}
      <Stack.Screen name='Login' component={LoginScreen}/>
      <Stack.Screen name='Rules' component={RulesScreen}/>

      
    </Stack.Navigator>

  )
}

function CustomDrawerContent(Props) {
  return (
    <SafeAreaView style={{ flex: 1 }}>
      <View style ={{padding:30}}></View>
        <Image
          source={require('C:/ReactNativeFinalProject/assets/FIFA_Flag_blue.png')}
          style={styles.sideMenuProfileIcon}
        />
      <View style ={{padding:30}}></View>

      <DrawerContentScrollView {...Props}>
        <DrawerItemList {...Props} />
        <DrawerItem
          label="CloseDrawer"
          onPress={() => Props.navigation.closeDrawer()}
        />
        {/* <DrawerItem label="toggleDrawer" onPress={() => Props.navigation.toggleDrawer()} /> */}
      </DrawerContentScrollView>
    </SafeAreaView>
  );
}

const Drawer = createDrawerNavigator();

function MyDrawer() {
  return (
    <Drawer.Navigator
      useLegacyImplementation
      drawerContent={(Props) => <CustomDrawerContent {...Props} />}
      screenOptions={{
        drawerStyle: {
          width: 300,
        },
        headerStyle:{backgroundColor:'#FFD66D'},
        headerTintColor:'#fff',
        headerTitleStyle:{fontWeight:'bold'}
      }}
    >
      <Drawer.Screen name="Login" component={LoginScreen} />
      <Drawer.Screen name="Home" component={HomeScreen} />
      {/* <Drawer.Screen name="Article" component={ArticleScreen} /> */}
      {/* <Drawer.Screen name="Detail" component={ProductStack} /> */}
      <Drawer.Screen name="Rules" component={RulesScreen} />
      <Drawer.Screen name="Role & Position" component={RoleScreen} />
      <Drawer.Screen name="Skill Move" component={SkillScreen} />
      <Drawer.Screen name="Bookmark" component={BookmarkScreen} />
      <Drawer.Screen name="Setting" component={SettingSrceen} />
      
    </Drawer.Navigator>
  );
}

export default function App() {
  return (
    <NavigationContainer theme={MyTheme}>
      <MyDrawer/>
    </NavigationContainer>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#fff",
    alignItems: "center",
    justifyContent: "center",
  },
  sideMenuProfileIcon: {
    resizeMode: "center",
    width: 100,
    height: 100,
    borderRadius: 100 / 2,
    alignSelf: "center",
  },
});
