import {
    StyleSheet,
    Text,
    View,
    Button,
    TextInput,
    Image,
    SafeAreaView,
    ImageBackground
  } from "react-native";
  
  import HomeScreen from "./HomeScreen";
  import RulesScreen from "./RulesScreen";
  import RoleScreen from "./RoleScreen";
  import SkillScreen from "./SkillScreen";
  import BookmarkScreen from "./BookmarkScreen";
  import SettingSrceen from "./SettingSrceen";

  
  import { DefaultTheme } from "@react-navigation/native";

  
  import {
    createDrawerNavigator,
    DrawerContentScrollView,
    DrawerItem,
    DrawerItemList,
  } from "@react-navigation/drawer";
  import React from "react";

  
  const MyTheme = {
    ...DefaultTheme,
    colors: {
      ...DefaultTheme.colors,
      primary: "rgb(26,45,85)",
      background: "#F2F2F2",
      
     
    },
  };
  
  function CustomDrawerContent(Props) {
    return (
      <SafeAreaView style={{ flex: 1 }}>
        <View style ={{padding:15}}></View>
          <Image
            source={require('c:/ReactNativeFinalProject/assets/FIFA_Flag_blue.png')}
            style={styles.sideMenuProfileIcon}
          />
        <View style ={{padding:15}}></View>
  
        <DrawerContentScrollView {...Props}>
          <DrawerItemList {...Props} />
          <DrawerItem
            label="Logout"
            onPress={() => navigation.navigate('Bookmark')}
          />
          {/* <DrawerItem label="toggleDrawer" onPress={() => Props.navigation.toggleDrawer()} /> */}
        </DrawerContentScrollView>
      </SafeAreaView>
    );
  }
  
  const Drawer = createDrawerNavigator();
  
const DrawerScreen = () => {
  return (
   
        <Drawer.Navigator
            useLegacyImplementation
            drawerContent={(Props) => <CustomDrawerContent {...Props} />}
            screenOptions={{
            drawerStyle: {
                width: 300,
                alignItems : 'center',
                
            },
            headerStyle:{backgroundColor:'#FFD66D'},
            headerTintColor:'#fff',
            headerTitleStyle:{fontWeight:'bold'}
            }}
        >
            {/* <Drawer.Screen name="Login" component={LoginScreen} /> */}
            <Drawer.Screen name="Home" component={HomeScreen} options={{
            drawerIcon:  ({color}) =>(
            <Image source={require('c:/ReactNativeFinalProject/assets/Home.png')}
            style={{
            width: 30,
            height: 30,
            }}/>
            )
            }}/>
            <Drawer.Screen name="Rules" component={RulesScreen} options={{
            drawerIcon:  ({color}) =>(
            <Image source={require('c:/ReactNativeFinalProject/assets/Rules.png')}
            style={{
            width: 30,
            height: 30,
            }}/>
            )
            }}/>
            <Drawer.Screen name="Role & Position" component={RoleScreen} 
            options={{
            drawerIcon:  ({color}) =>(
            <Image source={require('c:/ReactNativeFinalProject/assets/Role.png')}
            style={{
            width: 30,
            height: 30,
            }}/>
            )
            }}/>
            <Drawer.Screen name="Skill Move" component={SkillScreen} 
            options={{
            drawerIcon:  ({color}) =>(
            <Image source={require('c:/ReactNativeFinalProject/assets/Skill.png')}
            style={{
            width: 30,
            height: 30,
            }}/>
            )
            }}/>
            <Drawer.Screen name="Bookmark" component={BookmarkScreen} 
            options={{
            drawerIcon:  ({color}) =>(
            <Image source={require('c:/ReactNativeFinalProject/assets/Bookmark.png')}
            style={{
            width: 30,
            height: 30,
            }}/>
            )
            }}/>
            <Drawer.Screen name="Setting" component={SettingSrceen} 
            options={{
            drawerIcon:  ({color}) =>(
            <Image source={require('c:/ReactNativeFinalProject/assets/Setting.png')}
            style={{
            width: 30,
            height: 30,
            }}/>
            )
            }}/>
            
        </Drawer.Navigator>
      
  )
}

export default DrawerScreen

const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: "#fff",
      alignItems: "center",
      justifyContent: "center",
    },
    sideMenuProfileIcon: {
      resizeMode: "center",
      width: 250,
      height: 200,
      borderRadius: 100 / 2,
      alignSelf: "center",
    },
})
